@login_required
def quiz(request):
    user = request.user
    chats = ChatBot.objects.filter(user=user)
    return render(request, "chat_bot_quiz.html", {"chats": chats})


@login_required
def chat(request):
    user = request.user
    chats = ChatBot.objects.filter(user=user)
    return render(request, "chat_bot.html", {"chats": chats})


